const e = $json;
const msgText = (e.message?.text || '').toString().trim();
const buttonId = (e.message?.buttonId || '').toString().trim();
const text = (e.message?.type === 'button') ? buttonId : msgText;

const enabled = (($env.ADMIN_WA_CONSOLE_ENABLED || 'false').toString().toLowerCase() === 'true');
const strictAr = (($env.STRICT_AR_OUT || 'true').toString().toLowerCase()==='true');
const hasArabic = /[ÿÄ-€ø›ê-›ø‡¢†-‡£ø]/.test(msgText || '');
const locale = (strictAr && hasArabic) ? 'ar' : 'fr';

const raw = (text || '').trim();
const isCmd = raw.startsWith('!');
const parts = raw.replace(/^!+/, '!').slice(1).trim().split(/\s+/).filter(Boolean);
const cmd = (parts[0] || '').toLowerCase();
const args = parts.slice(1);

return [{json:{
  ...e,
  adminConsole: {
    enabled,
    raw,
    isCmd,
    cmd,
    args,
    // for reply: keep original tail text
    tail: raw.startsWith('!reply') ? raw.replace(/^!reply\s+/i,'') : '',
    hasArabic,
    locale
  }
}}];

const e = $json;
const reply = '‚õîÔ∏è Acc√®s refus√©. (admin uniquement)';
return [{json:{...e, adminReplyText: reply}}];

const e = $json;
const cmd = (e.adminConsole?.cmd || '').toLowerCase();
const args = Array.isArray(e.adminConsole?.args) ? e.adminConsole.args : [];
const raw = (e.adminConsole?.raw || '').toString();

function usage() {
  return [
    '*Console Support + L10N*',
    '- !help',
    '- !tickets [open|all]',
    '- !take <ticket_id>',
    '- !close <ticket_id>',
    '- !reply <ticket_id> <message>',
    '',
    '*Templates (FR/AR)*',
    '- !template get <KEY> [fr|ar]',
    '- !template set <KEY> [fr|ar] <CONTENT...>',
    '- !template vars <KEY> [fr|ar] <var1,var2,...>',
    '',
    '*Delivery Zones*',
    '- !zone list',
    '- !zone set <wilaya> ; <commune> ; <fee_cents> ; <min_cents> ; <eta_min> ; <eta_max> ; <active:true|false>'
  ].join('\n');
}


function normLocale(x) {
  const l = (x || 'fr').toString().trim().toLowerCase();
  return (l.startsWith('ar')) ? 'ar' : 'fr';
}

let action = 'HELP';
let ticketId = null;
let filter = 'open';
let message = '';

let templateKey = null;
let templateLocale = 'fr';
let templateContent = '';
let templateVars = [];

let zoneWilaya = null;
let zoneCommune = null;
let zoneFee = null;
let zoneMin = null;
let zoneEtaMin = null;
let zoneEtaMax = null;
let zoneActive = null;
let zoneOp = null;

if (!cmd || cmd === 'help') action = 'HELP';
else if (cmd === 'tickets') { action = 'TICKETS'; filter = (args[0]||'open').toLowerCase(); }
else if (cmd === 'take') { action = 'TAKE'; ticketId = parseInt(args[0]||'0',10)||0; }
else if (cmd === 'close') { action = 'CLOSE'; ticketId = parseInt(args[0]||'0',10)||0; }
else if (cmd === 'reply') {
  action = 'REPLY';
  // raw tail: "<id> <message>"
  const tail = (e.adminConsole?.tail || '').trim();
  const m = tail.match(/^(\d+)\s+([\s\S]{1,2000})$/);
  ticketId = m ? (parseInt(m[1],10)||0) : 0;
  message = m ? m[2].trim() : '';
}
else if (cmd === 'template') {
  const sub = (args[0] || '').toLowerCase();
  templateKey = (args[1] || '').toString().trim();
  // locale can be args[2] if looks like fr/ar
  const maybeLoc = (args[2] || '').toString().trim();
  const hasLoc = /^(fr|ar)([-_].+)?$/i.test(maybeLoc);
  templateLocale = normLocale(hasLoc ? maybeLoc : 'fr');

  if (!templateKey) {
    action = 'UNKNOWN';
  } else if (sub === 'get') {
    action = 'TEMPLATE_GET';
  } else if (sub === 'set') {
    action = 'TEMPLATE_SET';
    // Parse tail: !template set KEY [loc] CONTENT...
    const m = raw.match(/^!template\s+set\s+([^\s]+)\s+((fr|ar)([-_].+)?)?\s*([\s\S]{0,4000})$/i);
    if (m) {
      templateKey = m[1].trim();
      templateLocale = normLocale(m[2]);
      templateContent = (m[5] || '').toString().trim();
    } else {
      templateContent = args.slice(2 + (hasLoc ? 1 : 0)).join(' ').trim();
    }
    if (templateContent.length > 2000) templateContent = templateContent.slice(0, 2000);
  } else if (sub === 'vars') {
    action = 'TEMPLATE_VARS';
    const rest = args.slice(2 + (hasLoc ? 1 : 0)).join(' ').trim();
    templateVars = rest.split(/[\s,]+/).map(s => s.trim()).filter(Boolean);
  } else {
    action = 'UNKNOWN';
  }
}

else if (cmd === 'zone' || cmd === 'delzone') {
  const sub = (args[0] || 'list').toLowerCase();
  zoneOp = sub;
  if (sub === 'list' || sub === 'ls') {
    action = 'ZONE_LIST';
  } else if (sub === 'set' || sub === 'add' || sub === 'upsert') {
    action = 'ZONE_SET';
    const m = raw.match(/^!zone\s+(set|add|upsert)\s+([\s\S]{1,500})$/i);
    const tail = (m ? m[2] : '').toString();
    const parts = tail.split(';').map(s => s.trim()).filter(Boolean);
    zoneWilaya = parts[0] || (args[1] || null);
    zoneCommune = parts[1] || (args[2] || null);
    zoneFee = parseInt(parts[2] || args[3] || '0', 10) || 0;
    zoneMin = parseInt(parts[3] || args[4] || '0', 10) || 0;
    zoneEtaMin = parseInt(parts[4] || args[5] || '45', 10) || 45;
    zoneEtaMax = parseInt(parts[5] || args[6] || '60', 10) || 60;
    const actRaw = (parts[6] || args[7] || 'true').toString().trim().toLowerCase();
    zoneActive = !(actRaw === 'false' || actRaw === '0' || actRaw === 'off' || actRaw === 'inactive');
    if (!zoneWilaya || !zoneCommune) action = 'UNKNOWN';
  } else if (sub === 'del' || sub === 'delete' || sub === 'rm') {
    action = 'ZONE_DELETE';
    const m = raw.match(/^!zone\s+(del|delete|rm)\s+([\s\S]{1,200})$/i);
    const tail = (m ? m[2] : '').toString();
    const parts = tail.split(';').map(s => s.trim()).filter(Boolean);
    zoneWilaya = parts[0] || (args[1] || null);
    zoneCommune = parts[1] || (args[2] || null);
    if (!zoneWilaya || !zoneCommune) action = 'UNKNOWN';
  } else {
    action = 'UNKNOWN';
  }
}

else action = 'UNKNOWN';

return [{json:{
  ...e,
  adminAction: action,
  adminTicketId: ticketId,
  adminFilter: filter,
  adminMessage: message,
  adminUsage: usage(),
  adminTemplateKey: templateKey,
  adminTemplateLocale: templateLocale,
  adminTemplateContent: templateContent,
  adminTemplateVars: templateVars,
  adminTemplateVarsJson: JSON.stringify(templateVars),
  adminZoneOp: zoneOp,
  adminZoneWilaya: zoneWilaya,
  adminZoneCommune: zoneCommune,
  adminZoneFeeCents: zoneFee,
  adminZoneMinCents: zoneMin,
  adminZoneEtaMin: zoneEtaMin,
  adminZoneEtaMax: zoneEtaMax,
  adminZoneActive: zoneActive
}}];

const rows = ($json.data || []);
if (!rows.length) {
  return [{json:{...$json, adminReplyText:'‚úÖ Aucun ticket.'}}];
}
let out = '*Tickets*\n';
for (const r of rows) {
  const id = r.ticket_id;
  const st = r.status;
  const reason = r.reason_code;
  const cust = r.customer_user_id;
  out += `#${id} ‚Äî ${st} ‚Äî ${reason} ‚Äî user:${cust}\n`;
}
out += '\nActions: !take <id> | !reply <id> <msg> | !close <id>';
return [{json:{...$json, adminReplyText: out}}];

const e = $json;
const row = (e.data && e.data[0]) ? e.data[0] : null;
if (!row || !e.adminMessage) {
  return [{json:{...e, customerOutbox: null, adminReplyText: '‚ö†Ô∏è Utilisation: !reply <ticket_id> <message>'}}];
}
const dedupe = `support_reply:${row.ticket_id}:${e.inbound_message_id || e.message?.id || Date.now()}`;
const payload = {
