    payload_json: payload,
    dedupe_key: dedupe
  },
  adminReplyText: `‚úÖ Envoy√© au client (ticket #${row.ticket_id}).`
}}];

const crypto = require('crypto');
const e = $json;
const txt = (e.adminReplyText || '').toString();
const adminConv = `t:${e.tenantId}:r:${e.restaurantId}:whatsapp:${e.userId}`;
