  conversation_key: adminConv,
  user_id: e.userId,
  template_key: 'TEXT',
  locale: (e.adminConsole?.locale || 'fr'),
  payload_json: payload,
  dedupe_key: dedupe
}}}];

const rows = ($json.data || []);
const key = ($json.adminTemplateKey || '').toString();
const loc = ($json.adminTemplateLocale || 'fr').toString();
if (!rows.length) {
  return [{json:{...$json, adminReplyText: `‚ùå Aucun template trouv√© pour *${key}* (${loc}).
‚û°Ô∏è Utilise: !template set ${key} ${loc} <contenu>`}}];
}
const r = rows[0];
const from = (r.tenant_id === '_GLOBAL') ? '_GLOBAL' : 'tenant';
const vars = Array.isArray(r.variables) ? r.variables : (r.variables || []);
let out = `üß© *Template* ${r.key} (${r.locale}) [source: ${from}]
`;
out += `Vars: ${(vars && vars.length) ? vars.join(', ') : '(none)'}

`;
out += r.content;
return [{json:{...$json, adminReplyText: out}}];

const rows = ($json.data || []);
if (!rows.length) {
  return [{json:{...$json, adminReplyText:'‚ùå Impossible de sauvegarder le template.'}}];
}
const r = rows[0];
const vars = Array.isArray(r.variables) ? r.variables : (r.variables || []);
