const menu = $input.item(0).json;
const brand = $input.item(1).json;
const userText = menu.user_text;

// Language detection (Darija patterns)
const darijaPatterns = /\b(sahit|bsaha|chhal|wesh|khouya|rabbi|bghit|3andi)\b/i;
const arabicPattern = /[\u0600-\u06FF]/;
const detectedLang = arabicPattern.test(userText) ? 'ar' : (darijaPatterns.test(userText) ? 'dz' : 'fr');

return {
  json: {
    user_text: userText,
    menu: menu.menu_formatted,
    menu_index: menu.menu_index,
    brand: brand,
    detected_lang: detectedLang
  }
};

