// Extract response from Ollama format
const ollamaResponse = $input.first().json;
const content = ollamaResponse.message?.content || ollamaResponse.response || '';

return {
  json: {
    response: content,
    model: ollamaResponse.model,
    done: ollamaResponse.done
  }
};
