// Input expected from CORE: {
//   channel, restaurantId, userId, replyText, buttons:[{id,title}], actions, debug
// }
const payload = $json;

const url = ($env.WA_SEND_URL || '').toString();
if (!url) {
  return [{json: {sent:false, reason:'WA_SEND_URL not set', payload} }];
}

const token = ($env.WA_API_TOKEN || '').toString();

// Generic send format (adapt to your provider / Meta Cloud API).
const body = {
  channel: 'whatsapp',
  to: payload.userId,
  restaurantId: payload.restaurantId,
  text: payload.replyText || '',
  buttons: Array.isArray(payload.buttons) ? payload.buttons : [],
  attachments: Array.isArray(payload.attachments) ? payload.attachments : []
};

const res = await $httpRequest({
  method: 'POST',
  url,
  headers: token ? { Authorization: 'Bearer ' + token } : {},
  body,
  json: true,
  timeout: 30000
});

return [{json: {sent:true, providerResponse: res, body} }];
