/**
 * P0-07 + P1-03 + P1-06: Generate Outbox Key and prepare message envelope
 * Supports both sync (direct send) and async (queue for worker) modes
 * P1-06: Propagate correlation_id for end-to-end tracing
 */
const crypto = require('crypto');
const payload = $json;
const timestamp = new Date().toISOString();

// P1-06: Get correlation_id from CORE (propagated through _timing)
const correlationId = payload._timing?.correlation_id || payload.correlation_id || crypto.randomUUID();

// P1-03: Check if async mode is enabled
const asyncEnabled = (($env.OUTBOX_ASYNC_ENABLED || 'false').toString().toLowerCase() === 'true');

// Generate unique outbox message ID
const outboxMsgId = payload.outboxMsgId || payload.msgId || crypto.randomUUID();
const outboxKey = `ralphe:outbox:wa:${outboxMsgId}`;
const outboxTtl = parseInt($env.OUTBOX_REDIS_TTL_SEC || '604800', 10); // 7 days default

// Prepare outbox entry with correlation_id
const outboxEntry = {
  channel: 'whatsapp',
  correlation_id: correlationId,
  outboxMsgId,
  outboxKey,
  createdAt: timestamp,
  payload: {
    userId: payload.userId,
    restaurantId: payload.restaurantId,
    replyText: payload.replyText,
    buttons: payload.buttons,
    attachments: payload.attachments,
    templateName: payload.templateName,
    templateParams: payload.templateParams,
    locale: payload.locale
  },
  status: 'pending',
  attempts: 0
};

return [{
  json: {
    ...payload,
    _timing: { ...payload._timing, correlation_id: correlationId },
    _outbox: {
      key: outboxKey,
      msgId: outboxMsgId,
      ttl: outboxTtl,
      entry: JSON.stringify(outboxEntry),
      asyncEnabled,
      correlation_id: correlationId
    }
  }
}];

/**
 * W5 - WhatsApp Outbound Sender
 * Supports both Mock API (dev) and Meta Cloud API (production)
 * 
 * Meta Cloud API format:
 *   POST https://graph.facebook.com/v21.0/{PHONE_NUMBER_ID}/messages
 *   Body: { messaging_product: 'whatsapp', to: 'PHONE', type: 'text', text: { body: 'MSG' } }
 * 
 * Input from CORE:
 *   { channel, restaurantId, userId, replyText, buttons, attachments, templateName, templateParams, locale }
 */

const payload = $json;
const timestamp = new Date().toISOString();

// Configuration
const graphVersion = ($env.GRAPH_API_VERSION || 'v21.0').toString().trim();
const sendUrl = ($env.WA_SEND_URL || '').toString().trim();
const phoneNumberId = ($env.WA_PHONE_NUMBER_ID || '').toString().trim();
const token = ($env.WA_API_TOKEN || '').toString().trim();
const maxRetries = parseInt($env.OUTBOX_MAX_ATTEMPTS || '3', 10);
const baseDelay = parseInt($env.OUTBOX_BASE_DELAY_SEC || '1', 10) * 1000;
const maxDelay = parseInt($env.OUTBOX_MAX_DELAY_SEC || '60', 10) * 1000;

// Detect if using Meta Cloud API (production) or mock
const isMetaApi = sendUrl.includes('graph.facebook.com') || !!phoneNumberId;
let url = sendUrl;

if (!url && phoneNumberId) {
  // Auto-construct Meta Cloud API URL
  url = `https://graph.facebook.com/${graphVersion}/${phoneNumberId}/messages`;
}

if (!url) {
  return [{ json: { 
    ...payload,
    sent: false, 
    error: 'WA_SEND_URL_NOT_SET',
    reason: 'WA_SEND_URL or WA_PHONE_NUMBER_ID must be configured',
    timestamp
  }}];
}

if (!token) {
  return [{ json: { 
    ...payload,
    sent: false, 
    error: 'WA_API_TOKEN_NOT_SET',
    reason: 'WA_API_TOKEN must be configured',
    timestamp
  }}];
}

const recipientPhone = (payload.userId || '').toString().trim();
if (!recipientPhone) {
  return [{ json: { 
    ...payload,
    sent: false, 
    error: 'RECIPIENT_MISSING',
    reason: 'userId (phone number) is required',
    timestamp
  }}];
}

// Build request body
let body;
if (isMetaApi) {
  // Meta Cloud API format
  const text = (payload.replyText || '').toString().trim();
  const templateName = (payload.templateName || '').toString().trim();

  if (templateName) {
    // Template message (for outbound outside 24h window)
    const templateParams = Array.isArray(payload.templateParams) ? payload.templateParams : [];
    const locale = (payload.locale || 'fr').toString();

    body = {
      messaging_product: 'whatsapp',
      recipient_type: 'individual',
      to: recipientPhone,
      type: 'template',
      template: {
        name: templateName,
        language: { code: locale === 'ar' ? 'ar' : 'fr' },
        components: templateParams.length > 0 ? [{
          type: 'body',
          parameters: templateParams.map(p => ({ type: 'text', text: String(p) }))
        }] : undefined
      }
    };
  } else if (Array.isArray(payload.buttons) && payload.buttons.length > 0) {
    // Interactive message with buttons
    body = {
      messaging_product: 'whatsapp',
      recipient_type: 'individual',
      to: recipientPhone,
      type: 'interactive',
      interactive: {
        type: 'button',
        body: { text: text || 'Choose an option:' },
        action: {
          buttons: payload.buttons.slice(0, 3).map((btn, i) => ({
            type: 'reply',
            reply: {
              id: btn.id || `btn_${i}`,
              title: (btn.title || btn.label || '').slice(0, 20)
            }
          }))
        }
      }
    };
  } else {
    // Simple text message
    body = {
      messaging_product: 'whatsapp',
      recipient_type: 'individual',
      to: recipientPhone,
      type: 'text',
      text: { 
        preview_url: false,
        body: text || '(empty message)'
      }
    };
  }
} else {
  // Mock API format (dev/test)
  body = {
    channel: 'whatsapp',
    to: recipientPhone,
    restaurantId: payload.restaurantId || '',
    text: payload.replyText || '',
    buttons: Array.isArray(payload.buttons) ? payload.buttons : [],
    attachments: Array.isArray(payload.attachments) ? payload.attachments : []
  };
}

// Send request with retry logic
let lastError = null;
let response = null;
let finalAttempt = 0;

for (let attempt = 1; attempt <= maxRetries; attempt++) {
  finalAttempt = attempt;
  try {
    response = await $httpRequest({
      method: 'POST',
      url,
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body,
      json: true,
      timeout: 30000,
      returnFullResponse: true
    });

    const statusCode = response.statusCode || response.status || 200;

    // Success
    if (statusCode >= 200 && statusCode < 300) {
      const responseBody = response.body || response.data || response;
      return [{ json: {
        ...payload,
        sent: true,
        provider: isMetaApi ? 'meta_cloud_api' : 'mock',
        messageId: responseBody?.messages?.[0]?.id || responseBody?.message_id || null,
        recipient: recipientPhone,
        timestamp,
        attempt: finalAttempt,
        response: responseBody
      }}];
    }

    // Rate limited - wait and retry
    if (statusCode === 429) {
      const retryAfter = parseInt(response.headers?.['retry-after'] || '60', 10);
      lastError = { statusCode, error: 'RATE_LIMITED', retryAfter };
      if (attempt < maxRetries) {
        await new Promise(r => setTimeout(r, Math.min(retryAfter * 1000, maxDelay)));
        continue;
      }
    }

    // Server error - retry with backoff
    if (statusCode >= 500) {
      lastError = { statusCode, error: 'SERVER_ERROR', body: response.body };
      if (attempt < maxRetries) {
        await new Promise(r => setTimeout(r, Math.min(baseDelay * Math.pow(2, attempt - 1), maxDelay)));
        continue;
      }
    }

    // Client error - don't retry
    if (statusCode >= 400) {
      return [{ json: {
        ...payload,
        sent: false,
        error: 'CLIENT_ERROR',
        statusCode,
        provider: isMetaApi ? 'meta_cloud_api' : 'mock',
        response: response.body || response,
        timestamp,
        attempt: finalAttempt,
        requestBody: body
      }}];
    }

  } catch (err) {
    lastError = { error: 'REQUEST_FAILED', message: err.message };
    if (attempt < maxRetries) {
      await new Promise(r => setTimeout(r, Math.min(baseDelay * Math.pow(2, attempt - 1), maxDelay)));
      continue;
    }
  }
}

// All retries exhausted
return [{ json: {
  ...payload,
  sent: false,
  error: 'MAX_RETRIES_EXHAUSTED',
  lastError,
  provider: isMetaApi ? 'meta_cloud_api' : 'mock',
  timestamp,
  attempts: maxRetries,
  requestBody: body
}}];

/**
 * P0-07 + P1-06: Prepare DLQ entry for failed message
 * P1-06: Include correlation_id for tracing
 */
const payload = $json;
const timestamp = new Date().toISOString();
const correlationId = payload._outbox?.correlation_id || payload._timing?.correlation_id || '';

const dlqEntry = {
  channel: 'whatsapp',
  correlation_id: correlationId,
  outboxMsgId: payload._outbox?.msgId,
  outboxKey: payload._outbox?.key,
  failedAt: timestamp,
  error: payload.error,
  lastError: payload.lastError,
  statusCode: payload.statusCode,
  attempts: payload.attempts || payload.attempt || 1,
  payload: {
    userId: payload.userId,
    restaurantId: payload.restaurantId,
    replyText: payload.replyText,
    buttons: payload.buttons,
    templateName: payload.templateName
  },
  requestBody: payload.requestBody,
  response: payload.response
};

return [{
  json: {
    ...payload,
    _dlq: {
      entry: JSON.stringify(dlqEntry),
      correlation_id: correlationId
    }
  }
}];

// P0-07 + P1-06: Final result node with correlation_id
const payload = $json;
const correlationId = payload._outbox?.correlation_id || payload._timing?.correlation_id || '';
return [{ json: {
  correlation_id: correlationId,
  sent: payload.sent,
  messageId: payload.messageId || null,
  recipient: payload.recipient || payload.userId,
  channel: 'whatsapp',
  outboxMsgId: payload._outbox?.msgId,
  error: payload.error || null,
  timestamp: new Date().toISOString()
}}];

// P0-07 + P1-06: Final result node (DLQ path) with correlation_id
const payload = $json;
const correlationId = payload._dlq?.correlation_id || payload._outbox?.correlation_id || payload._timing?.correlation_id || '';
return [{ json: {
  correlation_id: correlationId,
  sent: false,
  messageId: null,
  recipient: payload.userId,
  channel: 'whatsapp',
  outboxMsgId: payload._outbox?.msgId,
  error: payload.error,
  dlqPushed: true,
  timestamp: new Date().toISOString()
}}];

// P1-03 + P1-06: Async mode - message queued for worker with correlation_id
const payload = $json;
const correlationId = payload._outbox?.correlation_id || payload._timing?.correlation_id || '';
return [{ json: {
  correlation_id: correlationId,
  queued: true,
  sent: null,
  messageId: null,
  recipient: payload.userId,
  channel: 'whatsapp',
  outboxMsgId: payload._outbox?.msgId,
  mode: 'async',
  timestamp: new Date().toISOString()
}}];
