/**
 * P0-07 + P1-03 + P1-06: Generate Outbox Key and prepare message envelope
 * P1-06: Propagate correlation_id for end-to-end tracing
 */
const crypto = require('crypto');
const payload = $json;
const timestamp = new Date().toISOString();

// P1-06: Get correlation_id from CORE
const correlationId = payload._timing?.correlation_id || payload.correlation_id || crypto.randomUUID();

// P1-03: Check if async mode is enabled
const asyncEnabled = (($env.OUTBOX_ASYNC_ENABLED || 'false').toString().toLowerCase() === 'true');

const outboxMsgId = payload.outboxMsgId || payload.msgId || crypto.randomUUID();
const outboxKey = `ralphe:outbox:ig:${outboxMsgId}`;
const outboxTtl = parseInt($env.OUTBOX_REDIS_TTL_SEC || '604800', 10);

const outboxEntry = {
  channel: 'instagram',
  correlation_id: correlationId,
  outboxMsgId,
  outboxKey,
  createdAt: timestamp,
  payload: {
    userId: payload.userId,
    restaurantId: payload.restaurantId,
    replyText: payload.replyText,
    buttons: payload.buttons,
    attachments: payload.attachments
  },
  status: 'pending',
  attempts: 0
};

return [{
  json: {
    ...payload,
    _timing: { ...payload._timing, correlation_id: correlationId },
    _outbox: {
      key: outboxKey,
      msgId: outboxMsgId,
      ttl: outboxTtl,
      entry: JSON.stringify(outboxEntry),
      asyncEnabled,
      correlation_id: correlationId
    }
  }
}];

/**
 * W6 - Instagram DM Sender (Meta Graph API)
 * P0-07: Production-ready with retries, backoff, token masking
 * 
 * Instagram Messaging API:
 *   POST https://graph.facebook.com/{VERSION}/{PAGE_ID}/messages
 *   Body: { recipient: { id: 'IGSID' }, message: { text: 'MSG' } }
 */

const payload = $json;
const timestamp = new Date().toISOString();

// Configuration
const graphVersion = ($env.GRAPH_API_VERSION || 'v21.0').toString().trim();
const sendUrl = ($env.IG_SEND_URL || '').toString().trim();
const pageId = ($env.IG_PAGE_ID || '').toString().trim();
const token = ($env.IG_API_TOKEN || '').toString().trim();
const maxRetries = parseInt($env.OUTBOX_MAX_ATTEMPTS || '3', 10);
const baseDelay = parseInt($env.OUTBOX_BASE_DELAY_SEC || '1', 10) * 1000;
const maxDelay = parseInt($env.OUTBOX_MAX_DELAY_SEC || '60', 10) * 1000;

// Detect if using Meta API (production) or mock
const isMetaApi = sendUrl.includes('graph.facebook.com') || !!pageId;
let url = sendUrl;

if (!url && pageId) {
  url = `https://graph.facebook.com/${graphVersion}/${pageId}/messages`;
}

if (!url) {
  return [{ json: { 
    ...payload,
    sent: false, 
    error: 'IG_SEND_URL_NOT_SET',
    reason: 'IG_SEND_URL or IG_PAGE_ID must be configured',
    timestamp
  }}];
}

if (!token) {
  return [{ json: { 
    ...payload,
    sent: false, 
    error: 'IG_API_TOKEN_NOT_SET',
    reason: 'IG_API_TOKEN must be configured',
    timestamp
  }}];
}

const recipientId = (payload.userId || '').toString().trim();
if (!recipientId) {
  return [{ json: { 
    ...payload,
    sent: false, 
    error: 'RECIPIENT_MISSING',
    reason: 'userId (Instagram Scoped ID) is required',
    timestamp
  }}];
}

// Build request body for Meta Graph API
let body;
if (isMetaApi) {
  const text = (payload.replyText || '').toString().trim();

  if (Array.isArray(payload.buttons) && payload.buttons.length > 0) {
    // Quick replies (Instagram supports up to 13)
    body = {
      recipient: { id: recipientId },
      message: {
        text: text || 'Choose an option:',
        quick_replies: payload.buttons.slice(0, 13).map((btn, i) => ({
          content_type: 'text',
          title: (btn.title || btn.label || '').slice(0, 20),
          payload: btn.id || `btn_${i}`
        }))
      }
    };
  } else {
    body = {
      recipient: { id: recipientId },
      message: { text: text || '(empty message)' }
    };
  }
} else {
  // Mock API format
  body = {
    channel: 'instagram',
    to: recipientId,
    restaurantId: payload.restaurantId || '',
    text: payload.replyText || '',
    buttons: Array.isArray(payload.buttons) ? payload.buttons : [],
    attachments: Array.isArray(payload.attachments) ? payload.attachments : []
  };
}

// Send with exponential backoff retry
let lastError = null;
let response = null;
let finalAttempt = 0;

for (let attempt = 1; attempt <= maxRetries; attempt++) {
  finalAttempt = attempt;
  try {
    response = await $httpRequest({
      method: 'POST',
      url,
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body,
      json: true,
      timeout: 30000,
      returnFullResponse: true
    });

    const statusCode = response.statusCode || response.status || 200;

    // Success
    if (statusCode >= 200 && statusCode < 300) {
      const responseBody = response.body || response.data || response;
      return [{ json: {
        ...payload,
        sent: true,
        provider: isMetaApi ? 'meta_ig_api' : 'mock',
        messageId: responseBody?.message_id || responseBody?.recipient_id || null,
        recipient: recipientId,
        timestamp,
        attempt: finalAttempt,
        response: responseBody
      }}];
    }

    // Rate limited - wait and retry
    if (statusCode === 429) {
      const retryAfter = parseInt(response.headers?.['retry-after'] || '60', 10);
      lastError = { statusCode, error: 'RATE_LIMITED', retryAfter };
      if (attempt < maxRetries) {
        const delay = Math.min(retryAfter * 1000, maxDelay);
        await new Promise(r => setTimeout(r, delay));
        continue;
      }
    }

    // Server error - retry with backoff
    if (statusCode >= 500) {
      lastError = { statusCode, error: 'SERVER_ERROR', body: response.body };
      if (attempt < maxRetries) {
        const delay = Math.min(baseDelay * Math.pow(2, attempt - 1), maxDelay);
        await new Promise(r => setTimeout(r, delay));
        continue;
      }
    }

    // Client error - don't retry (except 429)
    if (statusCode >= 400) {
      // Mask token in error response for security
      const safeResponse = response.body || response;
      return [{ json: {
        ...payload,
        sent: false,
        error: 'CLIENT_ERROR',
        statusCode,
        provider: isMetaApi ? 'meta_ig_api' : 'mock',
        response: safeResponse,
        timestamp,
        attempt: finalAttempt,
        requestBody: body
      }}];
    }

  } catch (err) {
    lastError = { error: 'REQUEST_FAILED', message: err.message };
    if (attempt < maxRetries) {
      const delay = Math.min(baseDelay * Math.pow(2, attempt - 1), maxDelay);
      await new Promise(r => setTimeout(r, delay));
      continue;
    }
  }
}

// All retries exhausted
return [{ json: {
  ...payload,
  sent: false,
  error: 'MAX_RETRIES_EXHAUSTED',
  lastError,
  provider: isMetaApi ? 'meta_ig_api' : 'mock',
  timestamp,
  attempts: maxRetries,
  requestBody: body
}}];

/**
 * P0-07: Prepare DLQ entry for failed message
 * Token is masked for security
 */
const payload = $json;
const timestamp = new Date().toISOString();

const dlqEntry = {
  channel: 'instagram',
  outboxMsgId: payload._outbox?.msgId,
  outboxKey: payload._outbox?.key,
  failedAt: timestamp,
  error: payload.error,
  lastError: payload.lastError,
  statusCode: payload.statusCode,
  attempts: payload.attempts || payload.attempt || 1,
  payload: {
    userId: payload.userId,
    restaurantId: payload.restaurantId,
    replyText: payload.replyText,
    buttons: payload.buttons
  },
  requestBody: payload.requestBody,
  response: payload.response
};

return [{
  json: {
    ...payload,
    _dlq: {
      entry: JSON.stringify(dlqEntry)
    }
  }
}];

// P0-07: Final result node
const payload = $json;
return [{ json: {
  sent: payload.sent,
  messageId: payload.messageId || null,
  recipient: payload.recipient || payload.userId,
  channel: 'instagram',
  outboxMsgId: payload._outbox?.msgId,
  error: payload.error || null,
  timestamp: new Date().toISOString()
}}];

// P0-07: Final result node (DLQ path)
const payload = $json;
return [{ json: {
  sent: false,
  messageId: null,
  recipient: payload.userId,
  channel: 'instagram',
  outboxMsgId: payload._outbox?.msgId,
  error: payload.error,
  dlqPushed: true,
  timestamp: new Date().toISOString()
}}];

// P1-03: Async mode - message queued for worker
const payload = $json;
return [{ json: {
  queued: true,
  sent: null,
  messageId: null,
  recipient: payload.userId,
  channel: 'instagram',
  outboxMsgId: payload._outbox?.msgId,
  mode: 'async',
  timestamp: new Date().toISOString()
}}];
